book = {}


def prepare_book(path: str) -> None:
    with open(path, 'r', encoding='utf-8') as b:
        txt = b.read()
        k = 0
        n = 1
        while k < len(txt):
            s, l = _get_part_text(txt, k, 1050)
            book.update({n: s.lstrip()})
            n += 1
            k += l


prepare_book('book.txt')
